import React, { useContext, useState } from "react";
import axios, { AxiosError } from "axios";
import { AuthContext } from "./AuthContext";
import { AuthContextType } from "./interfaces/AuthContextType";
import { ErrorResponse } from "./interfaces/ErrorResponse";
import { useNavigate } from "react-router-dom";

function Profile() {
  const { user, setUser } = useContext(AuthContext) as AuthContextType;
const initialName = localStorage.getItem("name") || (user ? user.name : "");
  const [name, setName] = useState(initialName);

  const [avatarFile, setAvatarFile] = useState<File | null>(null);
  const url = "https://ifrbzeaz2b.execute-api.ap-northeast-1.amazonaws.com";

  const navigate = useNavigate();
  const handleAvatarChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setAvatarFile(e.target.files[0]);
    }
  };

  if (!user) {
    console.error("User is not authenticated");
    navigate("/login");
    return null;
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const response = await axios.put(
        `${url}/users`,
        {
          name: name,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
        }
      );

      // アイコンのアップロード
      let formData = new FormData();
      if (avatarFile) {
        formData.append("icon", avatarFile);
        const avatarUploadResponse = await axios.post(
          `${url}/uploads`,
          formData,
          {
            headers: {
              Authorization: `Bearer ${user.token}`,
              "Content-Type": "multipart/form-data",
            },
          }
        );

        if (avatarUploadResponse.status === 200) {
          const avatarUrl = avatarUploadResponse.data.iconUrl;
          if (user) {
            setUser({ ...user, iconUrl: avatarUrl, name: response.data.name });
            localStorage.setItem("avatarUrl", avatarUrl);
          }
        }
      } else {
        if (user) {
          setUser({ ...user, name: response.data.name });
        }
      }
    } catch (err: unknown) {
      const error = err as AxiosError<ErrorResponse>;
      if (error.response) {
        console.log(
          `Error ${error.response.data.ErrorCode}: ${error.response.data.ErrorMessageEN}`
        );
      } else {
        console.log("An error occurred while trying to update profile");
      }
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          ユーザー名：
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>

        <label>
          Avatar:
          <input type="file" accept="image/*" onChange={handleAvatarChange} />
        </label>

        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
}

export default Profile;
