import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import "./Home.css";
import { Book } from "./interfaces/Book";
import { AuthContext } from "./AuthContext";
import { Link } from "react-router-dom";

function Home() {
  const [books, setBooks] = useState<Book[]>([]);
  const [offset, setOffset] = useState(0);
  const [limit, setLimit] = useState(5);

  const authContext = useContext(AuthContext);

  // Define base URL
  const url = "https://ifrbzeaz2b.execute-api.ap-northeast-1.amazonaws.com";

  const fetchBooks = async () => {
    const endPoint = authContext?.isAuthenticated ? "/books" : "/public/books";
    const fullUrl = `${url}${endPoint}?offset=${offset}`;
    const headers = authContext?.isAuthenticated
      ? { Authorization: `Bearer ${localStorage.getItem("token")}` }
      : {};

    try {
      const response = await axios.get(fullUrl, { headers });

      if (response.status === 200) {
        setBooks(response.data);
      }
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, [offset, limit]);

  const renderBooks = () => {
    return (
      <div className="book-list__table-container">
        <table className="book-list__table">
          <thead className="book-list__table-head">
            <tr>
              <th>タイトル</th>
              <th>リンク</th>
              <th>詳細</th>
              <th>レビュー</th>
              <th>投稿者</th>
            </tr>
          </thead>
          <tbody className="book-list__table-body">
            {books.map((book) => (
              <tr key={book.id}>
                <td>{book.title}</td>
                <td>
                  <Link to={`/book/${book.id}`}>{book.title}</Link>
                </td>
                <td>{book.detail}</td>
                <td>{book.review}</td>
                <td>{book.reviewer}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  const handleNext = () => {
    setOffset((prevOffset) => prevOffset + 10);
  };
  const handleLimitChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setLimit(Number(e.target.value));
  };

  return (
    <div className="book-list">
      <div className="book-list__table-container">{renderBooks()}</div>
      <div className="book-list__next-button-container">
        <button className="book-list__next-button" onClick={handleNext}>
          次へ
        </button>
      </div>
    </div>
  );
}

export default Home;
