import React, { useEffect, useState } from "react";
import { Navbar, Nav } from "react-bootstrap";
import "./Navbar.css";
import { useContext } from "react";
import { AuthContext } from "./AuthContext";
import { AuthContextType } from "./interfaces/AuthContextType";
import axios from "axios";

function NavigationBar() {
  const { isAuthenticated, user, logout, avatarUrl, setAvatarUrl } = useContext(AuthContext) as AuthContextType;
  const [username, setUsername] = useState(null);
    // ユーザー認証後、ユーザー情報を取得
    useEffect(() => {
      if (isAuthenticated) {
        const token = "Bearer XXX"; // JWTトークンを取得（必要に応じて修正）

        axios.get('https://ifrbzeaz2b.execute-api.ap-northeast-1.amazonaws.com/users', {
          headers: { Authorization: token }
        })
        .then(response => {
          setUsername(response.data.name); // 取得したユーザー名を設定
        })
        .catch(error => {
          console.error('ユーザー情報の取得に失敗:', error);
        });
      }
    }, [isAuthenticated]);

  const handleLogout = () => {
    logout();
    localStorage.removeItem("avatarUrl");
    setAvatarUrl(null);
  };

  return (
    <Navbar className="blue-bg" variant="dark">
      <Navbar.Brand href="/" className="title">
        書籍レビューアプリ
      </Navbar.Brand>
      <Nav className="mr-auto">
        {!isAuthenticated && (
          <>
            <div>
              <Nav.Link className="menu" href="/signup">
                Sign Up
              </Nav.Link>
            </div>
            <div>
              <Nav.Link className="menu" href="/login">
                Log In
              </Nav.Link>
            </div>
          </>
        )}
        {isAuthenticated && (
          <>
            <div>{user && user.name}</div>
            <div>
              <Nav.Link className="menu" href="/profile">
                ユーザー情報編集
              </Nav.Link>
            </div>
            <div>
              <Nav.Link className="menu" onClick={handleLogout}>
                ログアウト
              </Nav.Link>
            </div>
          </>
        )}
      </Nav>
      <nav>
        <div className="navbar-end ml-auto">
          {isAuthenticated && avatarUrl ?(
            <img src={avatarUrl} alt="user avatar" style={{ width: '200px', height: 'auto' }} />
             ) :(
              <p>NULL</p>
             )}
        </div>
      </nav>
    </Navbar>
  );
}

export default NavigationBar;
